func:
//Create PVOUTPUT Message ;
msg.headers={};
//Create time
var date = new Date();
day = date.getDate();
if (day < 10) {day = "0" + day} 
month = date.getMonth()+1;
if (month < 10) {month = "0" + month}
year = date.getFullYear();
//node.warn(year);
hour = date.getHours();
if (hour < 10) {hour = "0" + hour}
minute = date.getMinutes();
if (minute < 10) {minute = "0" + minute}
//Create Headers
msg.headers["X-Pvoutput-Apikey"] = "your api key";
msg.headers["X-Pvoutput-SystemId"] = 12345          //"your system id";
msg.headers["content-type"]="application/x-www-form-urlencoded"
//Create Payload ]
d = ""+year+month+day;
t = hour+":"+minute; 
//Growatt publish pvenergytoday in 0.1 KW, PVOUTPUT.ORG expext watt hours.
v1 = msg.payload.values.pvenergytoday*100
v2 = msg.payload.values.pvpowerout/10
msg.payload = "&d="+d+"&t="+t+"&v1="+v1+"&v2="+v2;
//optional you can also sent other value to pvoutput.org 
//This can be done in a second flow also
//the flows will be combined by PVOUTPUT.ORG
//you have to retrieve value from (smart) energy  meter and outside temperature indicator
//v3 = 12345     //energy used today (watts)
//v4 = 1234.5    //energy used currently (watts)
//v5 = 12.3      //temeprature  
//v6 = 123.4     //voltage
//msg.payload = "&d="+d+"&t="+t+"&v3="+v3+"&v4="+v4+"&v5="+v5+"&v6="+v6;
return msg;
